LIST.SCRIPT:
local kSelf = UISelf;
local ListItem = kSelf:AddNewListItemChar("");
local kControl = ListItem:GetWnd();
local Size = kSelf:GetSize();
Size:SetX(Size:GetX() - 22);
kControl:SetSize(Size);
kControl:SetStaticTextW(GetTT(201024));
kControl:SetSize(kControl:GetTextSize());

