STATIC_FORM.HOTKEY:
local CardWnd = UISelf:GetParent():GetControl("FRM_CREATE_FIELD_SCENARIO"):GetControl("FRM_CARD0") if false == IsGrayDiff(CardWnd) then return true end
if UISelf:GetParent():GetControl("FRM_CREATE_FIELD_SCENARIO"):GetControl("FRM_CARD0"):GetControl("IMG_BG"):GetControl("IMG_CLEAR"):IsVisible() then iDiff = 6 else iDiff = 5 end Net_ReqEnterMission( iDiff, 0, 0, 0, 0, GetMutatotSelectedCount(), GetMutatorPacket() )
InitMission() UISelf:CloseParent() return true

FORM.SCRIPT:
local WndBG = UISelf:GetControl("IMG_BG1") if WndBG:IsNil() == true then return end local iMaxNum = 8 if 1 == WndBG:GetCustomDataAsInt() then StartDefenceBonusExp(UISelf, 0.2, iMaxNum) end
if 2 == WndBG:GetCustomDataAsInt() then StopDefenceBonusExp(UISelf, 0.4, iMaxNum)
end
